import {observable,action} from 'mobx'
class ChatStore{
    @observable chatFlag = false;
    @observable footerVisible = true;

    @action changeChatFlag(value){
        this.chatFlag = value
    }
    @action changeChatFlag(value){
        this.footerVisible = value
    }

}
let chatStore = new ChatStore()
const stores = {
    chatStore
}
export default stores
