import React,{useState} from 'react';
import Friend from '@components/friendItem'
import FriendDesc from '@components/friendItem/friendDsec'

export default (props) => {
    const friendInfo = [{
        imgSrc : '../../assets/profile/1.jpg',
        name : 'jack'
    },{
        imgSrc : '../../assets/profile/1.jpg',
        name : 'mary'
    }]
    const [flag, setflag] = useState(false)
    const [frienddesc, setfrienddesc] = useState(null)
    const handleClick = (item) => {
        setflag(true)
        setfrienddesc(item)
    }
    return (
        flag?
        friendInfo.map((item) => {
            return <Friend {...item} onClick={() => handleClick(item)} ></Friend>
        }):
        <FriendDesc></FriendDesc>
    )

}
